import React,{useState} from "react";
import {View,Button,TextInput,ScrollView,StyleSheet } from 'react-native'

import ImageSelector from "../components/ImageSelector"


const NewPlaceScreen =()=> {

     const [imagen,setImagenPadre] = useState()

     const [title,setTitle] = useState('')

     const handleTitleChange = text => setTitle (text);


return (
    <ScrollView>
        <View style={styles.container}>
            <TextInput style={styles.input} value={title} onChangeText={handleTitleChange}></TextInput>
            <ImageSelector onImage={image =>setImagenPadre(image)}/>

            <View style={styles.containerBoton}>
            <Button title="Go to Home" color={'black'}onPress={() => navigation.navigate('Home')}/>
            </View>

        </View>     
    </ScrollView>

)

}
const styles= StyleSheet.create({
    container:{
        flex:1,
        margin:30,

    },
    label:{
        fontSize:18,
        marginBottom:16
    },
    input:{
        borderBottomColor: '#ccc',
        borderBottomWidth: 1,
        marginBottom:16,
        paddingHorizontal:2,
        paddingVertical:4,
    },
    containerBoton: {
        padding:10,
    }
})
export default NewPlaceScreen
