import React, {useEffect, useState} from 'react'
import { View, StyleSheet, Text, TouchableOpacity, Image, Alert} from 'react-native';
import * as Location from 'expo-location';

import { fetchAddresses, insertAddress,fetchAddr } from '../db';

export const getLocation = () => {

    const [location, setLocation] = useState(null);

    const [street, setStreet] = useState(null);
    const [streetsSaved, setStreetsSaved] = useState([])

    const [mostrar,setMostrar] = useState(null)

    const getPermissionsLocation = async() => {


        const permission = await Location.requestForegroundPermissionsAsync();

        if (permission.status !== 'granted'){
            console.log(permission);
            return false
        }
        return true
    }

    const handleGetLocation = async () => {
        const isLocationOk = await getPermissionsLocation();

        if (!isLocationOk) return;

        const location = await Location.getCurrentPositionAsync({
            timeout: 5000,
        })


        setLocation({
            lat: location.coords.latitude,
            lng: location.coords.longitude,
        })
    }

    const convertLocation = async () => {
        const KEY_POSITION_STACK='32ffc23fc773c795dde3453c8ceeac6d'
        const response = await fetch(`http://api.positionstack.com/v1/reverse?access_key=${KEY_POSITION_STACK}&query=${location.lat},${location.lng}`);
        const data = await response.json();
        setStreet(data.data[0])
    }

    const saveLocation = async () => {
        const response = await insertAddress(street.label, location.lat, location.lng);
    }

    useEffect(()=> {
        ( async()=> {
            const rt=''
            const response = await fetchAddr(rt)

            setStreetsSaved(rt)

            console.log(rt+ 'aca')
        })()

    }, [])
    console.log('guardado')
   // console.log(streetsSaved[0]);
   //console.log(streetsSaved);
  // console.log(streetsSaved.length)
  console.log(streetsSaved +'aca')


//    console.log('adress individual')
//    if (streetsSaved.length !==0){
//     console.log(JSON.parse(JSON.stringify(streetsSaved[0])))
//     setMostrar(JSON.parse(JSON.stringify(streetsSaved[0])))
//    }


    const seeLocation =()=>{
        return (
            <View>
                <Text>{streetsSaved.length}</Text>
            </View>
        )
    }







    return (        
        <View style={styles.preview}>

            {!location ?
            <TouchableOpacity onPress={()=> handleGetLocation()} style={styles.appButtonContainer}>
                <Text style={styles.texto}>Get location</Text>
            </TouchableOpacity>
            : <>
            <View  style={styles.container}>
                <Text> Latitud: {location.lat}, longitud: {location.lng} </Text>
            </View>

            <TouchableOpacity onPress={()=> convertLocation()} style={styles.appButtonContainer}>
                <Text style={styles.texto}>See street</Text>
            </TouchableOpacity>

            {street&&
                <>
                    <Text>{street.label}</Text>
                    <TouchableOpacity onPress={()=> saveLocation()} style={styles.appButtonContainer}>
                        <Text style={styles.texto}>Save all in SQLite</Text>

                    </TouchableOpacity>
                </>
            }
            </>

            }
        </View>


    )
}

const styles = StyleSheet.create({
    preview: {
        width: '100%',
        height: 200,
        marginBottom: 10,
        justifyContent: 'center',
        alignItems: 'center',

    },
    container:{
        flex:1, 
        shadowColor:'black',
        shadowOpacity: 0.26,
        shadowOffset: {width:0,height:2},
        shadowRadius:6,
        elevation:8,
        justifyContent: 'center',
        alignItems: 'center',
        padding:8,
    },
    imagen: {
        width: '100%',
        height: '100%',
    },
    appButtonContainer: {
        backgroundColor: "#000",
        paddingVertical: 10,
        paddingHorizontal: 12,
        borderRadius: 2,
        marginBottom: 10,
      },
      texto:{
        color:'white'
      }
})
